import { Component, OnInit } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  honorifics = ['Dr.', 'Mr.', 'Mrs.', 'Ms.'];
  signupForm!: FormGroup;
  forbiddenUsernames = ['john.doe', 'jane.doe'];
  title: any;
  value: any;

  constructor(private fb: FormBuilder) {}

  ngOnInit() {
    this.signupForm = new FormGroup({
      'userData': new FormGroup({
        'username': new FormControl(),
        'email': new FormControl()
      }),
      'honorific': new FormControl('Mrs.'),
      'interests': new FormArray([])
    });

    this.signupForm.statusChanges.subscribe(
      (value) => console.log(value)
    );
    this.signupForm.setValue({
      'userData': {
        'username': 'danblakeman',
        'email': 'dcblakeman@gmail.com'
      },
      'honorific': 'male',
      'interests': []
    });

    this.signupForm.patchValue({
      'userData': {
        'username': 'danblakeman',
      }
    });
  }

  onSubmit() {
    console.log(this.signupForm);
    this.signupForm.reset();
  }

  onAddInterest() {
    const control = new FormControl(Validators.required);
    (<FormArray>this.signupForm.get('interests')).push(control);
  }

  forbiddenNames(control: FormControl): {[s: string]: boolean} {
    if (this.forbiddenUsernames.indexOf(control.value) !== -1) {
      return {'nameIsForbidden': true};
    }
    return $any(null);
  }

  forbiddenEmails(control: FormControl): Promise<any> | Observable<any> {
    const promise = new Promise<any>((resolve, reject) => {
      setTimeout(() => {
        if (control.value === 'test@test.com') {
          resolve({'emailIsForbidden': true});
        } else {
          resolve(null);
        }
      }, 1000);
    });
    return promise;
  }
}
function $any(arg0: null): { [s: string]: boolean; } {
  throw new Error('Function not implemented.');
}
