import { Component, OnInit , HostListener} from '@angular/core';
import { HttpClient, HttpParams, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs'

interface Properties {
  name: string;
  region: string;
  incomeLevel: string;
  capital: string;
  longitude: number;
  latitude: number;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent {
  title = 'svg-interactive-map';

  readonly ROOT_URL = 'http://api.worldbank.org/v2/country/'

  properties: any;

  constructor(private http: HttpClient) {}

  @HostListener('click', ['$event.target.id'])
  handleClick(id: string) {
    console.log(id)
    if(id === '') {
      return null;
    }
    else {
      this.http.get(this.ROOT_URL + id + "?format=json").subscribe(res => {
      this.properties = res;
      console.log(typeof(this.properties))
      });
    }

  }

}
<div class="container">
  <div class="column">
    <h2>Map</h2>
    <app-map></app-map>
  </div>
  <div class="column">
    <h2>Description</h2>
    <pre id="json">
      <ul>

      </ul>*ngFor='let property of properties'>

        <td>{{ property[0] | json }}</td>
    </pre>
</div>

